inline int getPar(int v) {
	int hp = d[v] - n + 2 * sz[v];
	auto tmp = lower_bound(b, b + n, pii(hp, 0)) - b;
	if (b[tmp].first != hp) {
		cout << -1;
		exit(0);
	}
	return b[tmp].second;
}

int dfs(int v) {
	int res = 0;
	for (auto u : adj[v])
		res += dfs(u) + sz[u];
	return res;
}

