const int MAXN = 1e5 + 50;
ll d[MAXN], pa[MAXN], sz[MAXN];

int main() {
	int n; scanf("%d", &n);
	for(int i = 1; i <= n; i++)
		scanf("%lld", &d[i]);

	map<ll, int> M;
	for(int i = 1; i <= n; i++)
		M[d[i]] = i;
	for(int i = 1; i <= n; i++)
		sz[i] = 1;

	vector<int> v;
	for(int i = 1; i <= n; i++) v.push_back(i);
	sort(v.begin(), v.end(), [](int i, int j){ return d[i] > d[j]; });
	int root = v.back();
	for(auto c : v) {
		if(c == root) break;
		ll dval = d[c] + 2*sz[c] - n;
		if(M.find(dval) == M.end() || M[dval] == c) {
			printf("-1");
			exit(0);
		}
		int p = M[dval];
		pa[c] = p;
		sz[p] += sz[c];
	}

	for(int i = 1; i <= n; i++)
		if(i != root) printf("%d %d\n", i, pa[i]);
}

