#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const int maxn = 1e5;
int n;

struct data {
	ll x; int id;
	bool operator < (const data &t) const {
		return x < t.x;
	}
}a[maxn + 10];
ll b[maxn + 10], c[maxn + 10], dep[maxn + 10], d[maxn + 10];
int sz[maxn + 10];
vector<int> g[maxn + 10];

void dfs1(int p) {
	c[a[1].id] += dep[p]; sz[p] = 1;
	for (int i = 0; i < (int)g[p].size(); ++i) {
		int e = g[p][i];
		dep[e] = dep[p] + 1;
		dfs1(e); 
		sz[p] += sz[e];
	}
}

void dfs2(int p) {
	for (int i = 0; i < (int)g[p].size(); ++i) {
		int e = g[p][i];
		c[e] = c[p] + n - 2 * sz[e];
		dfs2(e);
	}
}

int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i) {
		scanf("%lld", &a[i].x);
		d[i] = a[i].x; a[i].id = i;
	}
	sort(a + 1, a + n + 1);
	for (int i = 1; i <= n; ++i) {
		b[i] = a[i].x; sz[i] = 1;
	}
	for (int i = n; i > 1; --i) {
		ll v = b[i] - n + 2 * sz[i];
		int p = lower_bound(b + 1, b + i, v) - b;
		if (p < i && b[p] == v) {
			g[a[p].id].push_back(a[i].id);
			sz[p] += sz[i];
		} else {
			printf("-1"); return 0;
		}
	}
	dfs1(a[1].id);
	dfs2(a[1].id);
	for (int i = 1; i <= n; ++i)
		if (d[i] != c[i]) {
			printf("-1"); return 0;
		}
	for (int i = 1; i <= n; ++i)
		for (int j = 0; j < (int)g[i].size(); ++j)
			printf("%d %d\n", i, g[i][j]);
}

