//Union-Find
class UnionFind {
private:
	std::vector<int> parent;
	std::vector<int> height;
	std::vector<int> m_size;

public:
	UnionFind(int size_) : parent(size_), height(size_, 0), m_size(size_, 1) {
		for (int i = 0; i < size_; ++i) parent[i] = i;
	}
	void init(int size_) {
		parent.resize(size_);
		height.resize(size_, 0);
		m_size.resize(size_, 0);
		for (int i = 0; i < size_; ++i) parent[i] = i;
	}
	int find(int x) {
		if (parent[x] == x) return x;
		return parent[x] = find(parent[x]);
	}
	void unite(int x, int y) {
		x = find(x);
		y = find(y);
		if (x == y) return;
		int t = size(x) + size(y);
		m_size[x] = m_size[y] = t;
		if (height[x] < height[y]) parent[x] = y;
		else parent[y] = x;
		if (height[x] == height[y]) ++height[x];
	}
	bool same(int x, int y) {
		return find(x) == find(y);
	}
	int size(int x) {
		if (parent[x] == x) return m_size[x];
		return size(parent[x] = find(parent[x]));
	}
};


std::vector<int> makeD(std::vector<std::vector<int>> g) {
	int n = g.size();

	std::vector<int> sz(n, 1);
	{
		std::function<void(int, int)> dfs = [&](int v, int par) {
			for (auto& to : g[v]) if(to != par) {
				dfs(to, v);
				sz[v] += sz[to];
			}
		};
		dfs(0, -1);
	}

	std::vector<int> d(n, 0);
	{ // init d[0]
		std::vector<int> depth(n, 0);
		std::function<void(int, int)> dfs = [&](int v, int par) {
			d[0] += depth[v];
			for (auto& to : g[v]) if (to != par) {
				depth[to] = depth[v] + 1;
				dfs(to, v);
			}
		};
		dfs(0, -1);
	}
	{
		std::function<void(int, int)> dfs = [&](int v, int par) {
			for (auto& to : g[v]) if (to != par) {
				d[to] = d[v] + n - 2 * sz[to];
				dfs(to, v);
			}
		};
		dfs(0, -1);
	}
	return d;
}


