#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
const int MAXN = 100010;

struct Edge
{
	int to, nxt;
}e[MAXN << 1];
int tot, h[MAXN];

inline void add(int u, int v)
{
	e[++tot] = (Edge){v, h[u]};
	h[u] = tot;
}

struct Node
{
	int id; ll val;
	bool operator < (const Node &x) const
	{
		return val < x.val;
	}
}d[MAXN];

int n, fa[MAXN], vis[MAXN], siz[MAXN];
map<ll, int> H; ll sum;

bool solve(int x)
{
	if(x == 1) return 1;
	ll now = d[x].val + 2ll * siz[x] - n;
	if(H.find(now) == H.end()) return 0;
	int id = H[now];
	if(!siz[id]) ++siz[id];
	siz[id] += siz[x];
	fa[x] = id;
	add(id, x), add(x, id);
	if(vis[id]) return 1;
	vis[id] = vis[x] = 1;
	return solve(id);
}

void dfs(int x, ll res)
{
	sum += res;
	for(int i = h[x]; i; i = e[i].nxt)
	{
		int y = e[i].to;
		if(y == fa[x]) continue;
		dfs(y, res + 1);
	}
}

int main()
{
	scanf("%d", &n);
	for(int i = 1; i <= n; ++i)
	{
		scanf("%lld", &d[i].val);
		d[i].id  = i;
	}
	sort(d + 1, d + 1 + n);
	for(int i = 1; i <= n; ++i) H[d[i].val] = i;
	for(int i = n; i > 1; --i)
		if(!vis[i])
		{
			siz[i] = 1;
			if(!solve(i)) return puts("-1"), 0;
		}
	dfs(1, 0);
	if(sum != d[1].val) return puts("-1"), 0;
	for(int i = 2; i <= n; ++i) printf("%d %d\n", fa[i], i);
	return 0;
}

