#include<bits/stdc++.h>
#define	INF	0x3f3f3f3f
#define	LL	long long
#define	MAXN	100010
using namespace std;
LL minn;
int n, size[MAXN], fa[MAXN], F[MAXN];
struct info{LL w; int id;}a[MAXN];

bool operator < (info a, info b){
	return a.w < b.w;
}

template <typename T> void chkmin(T &x, T y){x = min(x, y);}
template <typename T> void chkmax(T &x, T y){x = max(x, y);}
template <typename T> void read(T &x){
	x = 0; int f = 1; char ch = getchar();
	while (!isdigit(ch)) {if (ch == '-') f = -1; ch = getchar();}
	while (isdigit(ch)) {x = x * 10 + ch - '0'; ch = getchar();}
	x *= f;
}

int find(int x){
	if (fa[x] == x) return fa[x];
	else return (fa[x] = find(fa[x]));
}

int merge(int x, int y){
	int fx = find(x), fy = find(y);
	if (fx == fy) return 0;
	fa[fx] = fy;
	return 1;
}

int main(){
	read(n);
	minn = 1ll * INF * INF;
	for (int i = 1; i <= n; ++i){
		read(a[i].w), a[i].id = i;
		chkmin(minn, a[i].w);
	}
	for (int i = 1; i <= n; ++i)
		size[i] = 1, fa[i] = i;
	sort(a + 1, a + 1 + n);
	for (int i = n; i > 1; --i){
		int cur = a[i].id;
		LL vf = a[i].w + 2 * size[cur] - n;
		//printf("%d %lld\n", cur, vf);
		int f = lower_bound(a + 1, a + 1 + n, (info){vf, 0}) - a;
		if (a[f].w != vf) {printf("-1\n"); return 0;}
		f = a[f].id;
		//printf("%d\n", f);
		int fla = merge(cur, f);
		if (!fla) {printf("-1\n"); return 0;}
		F[cur] = f;
		size[f] += size[cur];
	}
	for (int i = 1; i <= n; ++i){
		if (a[i].w == minn) continue;
		printf("%d %d\n", a[i].id, F[a[i].id]);
	}
	return 0;
}

