void qsort(int l,int r)
{
	int i,j;
	node m,t;
	i=l;j=r;
	m=a[(l+r)/2];
	while (i<=j)
	{
		while (a[i].d>m.d) i++;
		while (a[j].d<m.d) j--;
		if (i<=j)
		{
			t=a[i];a[i]=a[j];a[j]=t;
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (i<r) qsort(i,r);
}

