int find(int x){
	if (fa[x] == x) return fa[x];
	else return (fa[x] = find(fa[x]));
}

int merge(int x, int y){
	int fx = find(x), fy = find(y);
	if (fx == fy) return 0;
	fa[fx] = fy;
	return 1;
}

