void solve() {
  int n;
  cin >> n;
  for(int i = 0; i < n; i++) cin >> d[i];
  sort(d, d+n);
  map<ll, int> from;
  for(int i = 0; i < n; i++) from[d[i]] = i;
  for(int i = 0; i < n; i++) treesz[i] = 1;
  for(int i = n-1; i >= 1; i--) {
    ll currDist = d[i];
    ll want = currDist - n + 2 * treesz[i];
    if(!from.count(want) || from[want] >= i) {
      cout << "-1\n";
      return;
    }
    parent[i] = from[want];
    treesz[parent[i]] += treesz[i];
  }
  for(int i = 1; i < n; i++) {
    dp[i] = dp[parent[i]] + 1;
  }
  ll total = 0;
  for(int i = 0; i < n; i++) {
    total += dp[i];
  }
  if(total != d[0]) {
    cout << "-1\n";
    return;
  }
  for(int i = 1; i < n; i++) {
    cout << (parent[i]+1) << " " << (i+1) << "\n";
  }
}

