const int N = 100010;
struct Edge{
	int data, next;
}e[N * 2];
struct Node{
	int id, num;
}p[N];
int n, size[N], eu[N], ev[N], head[N], place, cnt;
ll num[N], dis;
void build(int u, int v){
	e[++place].data = v; e[place].next = head[u]; head[u] = place;
}
void dfs(int x, int fa, int di){
	dis += di;
	for (int ed = head[x]; ed != 0; ed = e[ed].next)
		if (e[ed].data != fa)
			dfs(e[ed].data, x, di + 1);
}
void error(){
	printf("-1\n");
	exit(0);
}
bool cmp(Node x, Node y){
	return x.num < y.num;
}
int main(){
	read(n);
	for (int i = 1; i <= n; i++){
		read(num[i]), size[i]++;
		p[i].num = num[i];
		p[i].id = i;
	}
	sort(num + 1, num + n + 1);
	sort(p + 1, p + n + 1, cmp);
	for (int i = n; i >= 1; i--){
		int tmp = num[i] - (n - 2 * size[i]);
		if (tmp >= num[i]) continue;
		ll id = lower_bound(num + 1, num + n + 1, tmp) - num;
		if (num[id] == tmp){
			build(id, i); build(i, id);
			eu[++cnt] = id, ev[cnt] = i;
			size[id] += size[i];
		}
		else error();
	}
	dfs(1, 0, 0);
