int n, num[MX], par[MX];
ll d[MX], ans;
vi rpar[MX];
map<ll,int> m;

void gen(int x, int d = 0) {
    ans += d;
    for (int i: rpar[x]) gen(i,d+1);
}

int main() {
    io();
    cin >> n;

    priority_queue<pl> q;
    FOR(i,1,n+1) {
        cin >> d[i];
        num[i] = 1;
        m[d[i]] = i;
        q.push({d[i],i});
    }
    while (1) {
        auto a = q.top(); q.pop();
        par[a.s] = m[a.f-n+2*num[a.s]];
        if (2*num[a.s] >= n) par[a.s] = 0;
        if (par[a.s] == 0) {
            if (sz(q) != 0) {
                cout << -1;
                exit(0);
            } else {
                gen(a.s);
                if (d[a.s] != ans) {
                    cout << -1;
                    exit(0);
                }
                FOR(i,1,n+1) if (par[i]) cout << i << " " << par[i] << "\n";
                exit(0);
            }
        } else {
            rpar[par[a.s]].pb(a.s);
            num[par[a.s]] += num[a.s];
        }
    }
}

