void solve() {
  int n; cin >> n;
  vector<pair<ll, int>> g(n);
  rep(i,n) {
    cin >> g[i].first;
    g[i].second = i;
  }
  sort(ALL(g));

  vi cnt(n);
  vector<pii> ans; ans.reserve(n-1);

  rrep1(i,n) {
    ll d; int idx;
    tie(d, idx) = g[i];

    ll nd = d - (n-2) + 2*cnt[idx];
    auto itr = lower_bound(ALL(g), make_pair(nd, 0));

    if (itr->first != nd || nd == d) {
      cout << -1 << endl;
      return;
    }

    int nidx = itr->second;
    cnt[nidx] += cnt[idx] + 1;
    ans.emplace_back(idx+1, nidx+1);
  }

  cout << ans << endl;
}


