const int maxn=100005;
int n;
int par[maxn],sz[maxn],ord[maxn];
ll d[maxn];
map<ll,int>rev;
bool cmp(int a,int b){
	return d[a]<d[b];
} 

int main(){
	scanf("%d",&n);
	REP(i,n){
		scanf("%lld",&d[i]);
		ord[i]=i;
		rev[d[i]]=i;
	}
	sort(ord+1,ord+1+n,cmp);
	for(int i=n;i>1;i--){
		int x=ord[i];
		if(sz[x]+1>n||!rev.count(2*(sz[x]+1)+d[x]-n)){
			puts("-1");
			return 0;
		}
		int y=rev[2*(sz[x]+1)+d[x]-n];
		par[x]=y;
		sz[y]+=sz[x]+1;
	}
	REP(i,n)if(!par[i]){
		if(sz[i]+1!=n){
			puts("-1");
			return 0;
		}
	}
	REP(i,n)if(par[i])printf("%d %d\n",par[i],i);
	return 0;
}

