	static Node node[MAX];
	cin >> N;
	for (int i = 0;i < N;++ i) {
		cin >> node[i].num;
		node[i].id = i + 1;
		node[i].height = 1;
	}
	sort(node, node + N);

	int64_t sum = 0; // 1
	for (int i = N - 1;i > 0;-- i) { // 
		int min = 0, max = i - 1;
		int64_t check = node[i].num - N + (node[i].height << 1);
		for (int mid = i >> 1;max - min > 1;mid = min + (max - min >> 1)) {
			if (node[mid].num > check) max = mid;
			else min = mid;
		}
		if (node[min].num != check) { // 
			cout << -1;
			return 0;
		}
		node[i].edge = min;
		node[min].height += node[i].height;
		sum += node[i].height;
	}

	if (sum != node[0].num) {
		cout << -1;
		return 0;
	}
	for (int i = N - 1;i > 0;-- i) {
		cout << node[i].id << " " << node[node[i].edge].id << endl;
