const int MAXN = (1 << 20);

int n;
map<int64_t, int> id;
int64_t d[MAXN];

void read()
{
	cin >> n;
	for(int i = 1; i <= n; i++)
	{
		cin >> d[i];
		id[d[i]] = i;
	}
}

vector<pair<int, int> > ans;
vector<int> adj[MAXN];
int tr_sz[MAXN];

int64_t solve(int u)
{
	int64_t ret = tr_sz[u] - 1;
	for(auto i: adj[u])
		ret += solve(i);
	return ret;
}

void solve()
{
	for(int i = 1; i <= n; i++) tr_sz[i] = 1;

	while(id.size() > 1)
	{
		auto it = id.rbegin();
		int i = it->second;

		int64_t d_par = d[i] - n + 2 * tr_sz[i];

		if(!id.count(d_par) || d_par == d[i])
		{
			cout << -1 << endl;
			return;
		}

		int par = id[d_par];

		ans.push_back({par, i});
		adj[par].push_back(i);

		tr_sz[par] += tr_sz[i];
		id.erase(--id.end());
	}

	int root = id.begin()->second;
	if(solve(root) != d[root])
	{
		cout << -1 << endl;
		return;
	}

	for(auto it: ans) cout << it.first << " " << it.second << endl;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);

	read();
	solve();
	return 0;
}

