int main() {
	cin >> n;
	for (int i = 1; i <= n; ++i) cin >> d[i];
	multiset<pair<long long, int>> points;
	for (int i = 1; i <= n; ++i) points.insert({d[i], i});	
	bool fun = 1;
	auto root = points.begin();
	points.erase(points.begin());
	vector<pair<int, int>> edges;
	long long d_root = 0;
	while (not points.empty()) {
		auto q = points.begin();
		points.erase(q);
		fun &= (root->first - q->first + n) % 2 == 0;
		long long sz = (root->first - q->first + n) / 2; 
		fun &= sz < n;
		if (not fun) break;
		d_root += sz *1ll* (sz + 1) / 2;
		edges.push_back({root->second, q->second});
		long long temp_d = q->first;
		int last = q->second;
		for (int i = sz-1; i >= 1; --i) {
			long long cur_d = temp_d + n - 2 * i;
			auto u = points.lower_bound({cur_d, -1});
			fun &= u != points.end();
			if (not fun) break;
			points.erase(u);
			fun &= cur_d == u->first;
			if (not fun) break;	
			edges.push_back({last, u->second});
			temp_d = cur_d;
			last = u->second;
		}
		if (not fun) break;
	}
	fun &= d[root->second] == d_root;
	if (fun) {
		for (auto e : edges) {
			cout << e.first << " " << e.second << endl;
		}
	} else puts("-1");
	return 0;
}

