int findit(ll t){
	if (t<0) return -1;
	int l=0,r=n-1,mid;
	while (l<=r){
		mid=(l+r)>>1;
		if (sx[mid].fst==t) return mid;
		if (sx[mid].fst<t) l=mid+1;
		else r=mid-1;
	}
	return -1;
}
