bool vis[100005],rt;
int n,ansnum;
pair<int,int> ans[100005];
struct node
{
	long long val;
	int num;
};
node a[100005];
bool cmp(node x,node y)
{
	if (x.val>y.val) return 1;
	return 0;
}
bool cmp2(pair<int,int> a, pair<int,int> b)
{
	if (a.first<b.first) return 1;
	if (a.first>b.first) return 0;
	if (a.second<b.second) return 1;
	return 0;
}
int fd(int pos,long long v)
{
	int beg=pos,ed=n,md;
	while (beg<=ed)
	{
		md=(beg+ed)/2;
		if (a[md].val==v) return md;
		else if (a[md].val>v) beg=md+1;
		else ed=md-1;
	}
	if (a[md].val!=v) return -1;
}
void go(int pos,int dis)
{
	vis[pos]=1;
	int nex=fd(pos,a[pos].val-dis);
	//cout<<nex<<endl;
	if (nex>0)
	{
		ansnum++;
		if (a[pos].num<a[nex].num) ans[ansnum]=make_pair(a[pos].num,a[nex].num);
		else ans[ansnum]=make_pair(a[nex].num,a[pos].num);
		if (dis>1) go(nex,dis-2);
	}
	else
	{
		if (pos!=n)		//!!!!important! 
		{
			rt=0;
		}
	}
	return;
}
int main()
{
	rt=1;
	int i;
	scanf("%d",&n);
	for (i=1;i<=n;i++)
	{
		scanf("%d",&a[i].val);
		a[i].num=i;
	}
	sort(a+1,a+n+1,cmp);
	memset(vis,0,sizeof(vis));
	for (i=1;i<=n;i++)
	{
		if (!vis[i])
		{
			//cout<<i<<endl;
			go(i,n-2);
		}
	}
	if (ansnum!=n-1 || !rt)
	{
		printf("-1\n");
		return 0;
	}
	sort(ans+1,ans+ansnum+1,cmp2);
	for (i=1;i<=ansnum;i++)
	{
		printf("%d %d\n",ans[i].first,ans[i].second);
	}
	return 0;
}

