	for (const auto i : rep(0, n)) {
		que.emplace(d[i]);
		mp[d[i]] = i;
	}

	::std::vector<u64> csum(n, 0);

	while (que.size() != 1) {
		u64 c = que.top();que.pop();
		usize i = mp[c];
		if (n <= sub[i] * 2 || c < n - sub[i] * 2)
			return ::std::cout << "-1" << ::std::endl, 0;
		u64 next = c - (n - sub[i] * 2);
		if (mp.count(next)) {
			usize j = mp[next];
			parent[i] = j;
			csum[j] += csum[i] + sub[i];
			sub[j] += sub[i];
		}
		else {
			return ::std::cout << "-1" << ::std::endl, 0;
		}
	}
	const usize top = mp[que.top()];
	if (csum[top] != d[top])
		return ::std::cout << "-1" << ::std::endl, 0;

	for (const auto i : rep(0, n)) {
		if (parent[i] != n)
			::std::cout << i + 1 << " " << parent[i] + 1 << ::std::endl;
	}


	return 0;

}

