Int N;
vector<Int> D;
Graph G;

Int dfs(int u,int level = 0,int p = -1){
	Int ret = level;
	for(auto &&e:G[u]){
		if(e.dst==p)continue;
		ret += dfs(e.dst,level+1,u);
	}
	return ret;
}

void solve(){
	vector<pii> e;
//	sort(ALL(D));
//	reverse(ALL(D));
	vector<pair<Int,int> > vp;
	REP(i,D.size()){
		vp.push_back({D[i],i});
	}
	sort(ALL(vp));
	reverse(ALL(vp));
	map<Int,pair<int,int>> mp;//id, num of child
	REP(u,D.size()){
		mp[D[u]] = {u,1};
	}
	REP(t,D.size()-1){
		int i = vp[t].second;
		Int d = D[i];
		auto x = mp[d];
		Int d2 = d - (N - 2*x.second);
		auto it = mp.find(d2);
		if(it==mp.end()){
			fout<<-1<<endl;
			return;
		}
		it->second.second+=x.second;
		e.push_back({mp[d].first,it->second.first});
	}
	G.assign(N,Edges());
	for(auto &&p:e){
		addBiEdge(G,{p.first,p.second});
	}
	int umin = vp[D.size()-1].second;
	Int k = dfs(umin);
	if(k!=D[umin]){
		fout<<-1<<endl;
		return;
	}
	REP(i,e.size()){
		fout<<e[i].first+1<<" "<<e[i].second+1<<endl;
	}
	return;
}

