struct node
{
	ll d; int id;
	node(ll d = 0,int id = 0) : d(d) , id(id) {}
	friend bool operator < (const node &a,const node &b) { return a.d < b.d;}
} e[N];
int main()
{
	n = read();
	for(int i = 1;i <= n;i ++) e[i] = node(read(),i) , size[i] = 1;
	std::sort(e + 1,e + 1 + n);
	for(int i = n;i > 1;i --)
	{
		ll dfa = e[i].d + 2 * size[i] - n;
		int j = std::lower_bound(e + 1,e + 1 + n,node(dfa)) - e;
		if(e[j].d != dfa) return puts("-1") , 0;
		u[++ cnt] = e[i].id; v[cnt] = e[j].id;
		size[j] += size[i];
		dis += size[i];
	}
	if(dis != e[1].d) return puts("-1") , 0;
	else for(int i = 1;i <= cnt;i ++) printf("%d %d\n",u[i],v[i]);
	return 0;
