const int MAXN = (1 << 20);

int n, bg;
map<int64_t, int> id;
int64_t d[MAXN];

void read()
{
	cin >> n;
	for(int i = 1; i <= n; i++)
	{
		cin >> d[i];
		id[d[i]] = i;
	}
}

vector<pair<int, int> > ans;

void solve()
{
	vector<int> tr_sz(n + 1, 1);
	while(id.size() > 1)
	{
		auto it = id.rbegin();
		int i = it->second;

		int64_t d_par = d[i] - n + 2 * tr_sz[i];

		if(!id.count(d_par) || d_par == d[i])
		{
			cout << -1 << endl;
			return;
		}

		int par = id[d_par];

		ans.push_back({par, i});
		tr_sz[par] += tr_sz[i];

		id.erase(--id.end());
	}

	for(auto it: ans) cout << it.first << " " << it.second << endl;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);

	read();
	solve();
	return 0;
}

