int main(){
    int n; re(n);
    vl d(n);
    f0r(i, n) re(d[i]);
    set<pair<ll, int>, greater<pair<ll, int>>> s;
    f0r(i, n) s.insert(mp(d[i], 1));
    f0r(i, n) conv[d[i]] = i;
    while(sz(s)>1){
        auto ft = *s.begin();
        s.erase(ft);
        ll nxt = ft.f + 2*ft.s - n;
        auto it = s.lb(mp(nxt, INF));
        if(it == s.end()){
            ps(-1); return 0;
