        auto tmp = *it;
        if(tmp.f != nxt){
            ps(-1); return 0;
        }
        edges.eb(mp(conv[ft.f], conv[tmp.f]));
        s.erase(tmp);
        tmp.s += ft.s;
        s.insert(tmp);
    }
    for(auto e: edges) ps(e.f+1, e.s+1);
    return 0;
}

