#define MAX_N 200007
#define V std::vector

int N;
int sz[MAX_N];
ll d[MAX_N];
pr a[MAX_N];
V<int> G[MAX_N];
std::map<ll, int> mp;

void dfs1(int x, int las) {
    sz[x] = 1;
    for (int y : G[x])
        if (y != las) {
            dfs1(y, x);
            sz[x] += sz[y];
            d[x] += d[y] + sz[y];
        }
}

bool dfs2(int x, int las) {
    if (mp[d[x]] != x)
        return false;
    for (int y : G[x])
        if (y != las) {
            d[y] = d[x] + N - 2 * sz[y];
            if (!dfs2(y, x))
                return false;
        }
    return true;
}

void solve() {
    std::sort(a + 1, a + N + 1);
    Rep(i, N) sz[i] = 1, mp[a[i].fi] = a[i].se;
    ILep(i, N) {
        int u = a[i].se, s = N - 2 * sz[u], v;
        if (mp.count(a[i].fi - s)) {
            v = mp[a[i].fi - s];
            sz[v] += sz[u];
            G[u].push_back(v);
            G[v].push_back(u);
        } else
            return puts("-1"), void();
    }
    dfs1(1, 0);
    if (!dfs2(1, 0))
        return puts("-1"), void();
    Rep(i, N) {
        for (int j : G[i])
            if (i < j)
                printf("%d %d\n", i, j);
    }
}

int main() {
#ifdef LOCAL
    FIN("in");
#endif
    read(N);
    Rep(i, N) read(a[i].fi), a[i].se = i;
    solve();
    return 0;
}

