    vector<Pii> D(N);
    REP(i, N){
        cin >> D[i].first;
        D[i].second = i;
    }
    Sort(D); Reverse(D);

    vec g(N, -1);
    map<int, set<int>> par;
    vector<Pii> ans(0);

    REP(i, N){
        int d = D[i].first;
        int v = D[i].second;
        int cnt = 1;
        for(int c: par[d]){
            cnt += g[c];
            ans.push_back(Pii(v, c));
        }
        g[v] = cnt;
        par[d - (N - 2 * cnt)].insert(v);
    }

    Graph G(N);
    REP(i, ans.size()){
        G.add_edge2(ans[i].first, ans[i].second, 0);
    }
    int n = 0;
    bools used(N, false);
    G.dfs(n, used, 0);
    if(n == N && ans.size() == N - 1){
        REP(i, N - 1) cout << ans[i].first + 1 << " " << ans[i].second + 1 << endl;
    }else{
        cout << -1;
    }


    return 0;
}

