using namespace std;
typedef long long int ll;

const int MAXN = 1e5 + 5;
int n, par[MAXN], mn;
ll d, sz[MAXN], h[MAXN], sum = 0;
map <ll, ll> mp;
vector <int> adj[MAXN];
bool can = false, mark[MAXN];
vector <pair<ll, int>> v;

void dfs(int v, int parv){
	mark[v] = true;
	sum += h[v];
	for(auto u: adj[v]){
		if(!mark[u]){
			h[u] = h[v] + 1ll;
			dfs(u, v);
		}
		else if(u != parv)
			can = true;
	}
	return;
}

bool check(){
	for(int i = 1; i <= n; i++)
		if(i != mn && (par[i] < 1 || par[i] > n)){
			return true;
		}
	dfs(mn, -1);
	for(int i = 1; i <= n; i++)
		if(!mark[i])
			can = true;
	if(sum != -v[n - 1].first)
		can = true;
	return can;
}

