{
	ios_base::sync_with_stdio(false); cin.tie(0); cout.tie(0);
	cin >> n;
	for(ll i = 1; i <= n; i++){
		cin >> d; sz[i] = 1ll;
		mp[d] = i;
		v.push_back({-d, i});
	}
	sort(v.begin(), v.end());
	for(int i = 0; i < n - 1; i++){
		d = -v[i].first;
		ll ind = v[i].second;
		ll dpar = d + (2ll * sz[ind]) - (ll)n;
		if(!mp[dpar] || mp[dpar] == ind){
			cout << -1 << endl;
			return 0;
		}
		par[ind] = mp[dpar];
		sz[par[ind]] += sz[ind];
		adj[par[ind]].push_back(ind);
		adj[ind].push_back(par[ind]);
	}
	mn = v[n - 1].second;
	if(check()){
		cout << -1 << endl;
		return 0;
	}
	for(int i = 0; i < n - 1; i++){
		cout << par[v[i].second] << " " << v[i].second << '\n';
	}
	return 0; 
}

