	return c.val>b.val;
}
long long find(int k){
	long long chec=a[k].val+2*siz[k]-n;
	for(long long i=k+1;i<=n;i++) if(a[i].val==chec) return i;
	return -1;
}
int main(){
	priority_queue <ans> q;
	cin>>n;
	for(int i=1;i<=n;i++){
		scanf("%lld",&a[i].val);
		a[i].num=i;
		siz[i]=1;
	}
