		if(v==-1){
			cout<<-1<<endl;
			return 0;
		}
		siz[v]+=siz[i];
		q.push((ans) {min(a[v].num,a[i].num),max(a[v].num,a[i].num)});
	}
	while(!q.empty()){
		int v1=q.top().e1,v2=q.top().e2;
