int p[2];
vector<int> vert[2]; 

void init()
{
	scanf("%d",&n);
	for (int i=1;i<=n;++i)
	{
		pos[i]=i;
		scanf("%lld",&D[i]); 
		vert[D[i]&1].push_back(i);
	}
}

bool cmp(int i,int j)
{
	return D[i]<D[j];
}

int fa[maxn];

bool solve()
{
	sort(pos+1,pos+n+1,cmp);
	sort(vert[0].begin(),vert[0].end(),cmp);
	sort(vert[1].begin(),vert[1].end(),cmp);

	size[pos[1]]=n;
	++p[D[pos[1]]&1];

	for (int k=1;k<=n;++k)
	{
		int u=pos[k],cnt=1,c=(D[u]+n)&1;
		int &j=p[c],len=vert[c].size();
		for (;j<len&&cnt<size[u];++j)
		{
			int v=vert[c][j];
			size[v]=(D[u]+(LL)n-D[v])>>1;
			cnt+=size[v];
			fa[v]=u;
//			printf("%d %d\n",u,v);
//			cout<<u<<" "<<v<<" "<<size[v]<<" "<<cnt<<endl;
		}
//		cout<<u<<" "<<size[u]<<" "<<cnt<<endl;
		if (cnt!=size[u])
		{
//			cout<<"warning:"<<u<<endl;
			return 0;
		}
//		assert(cnt==size[u]);
	}

	for (int k=1;k<=n;++k)
	{
		int u=pos[k];
		if (fa[u])
			printf("%d %d\n",u,fa[u]);
	}
	return 1;
}

