  vector<vector<Int> > G(n);
  auto NO=[](){cout<<-1<<endl;exit(0);};
  auto add_edge=[&](Int x,Int y){
		  G[x].emplace_back(y);
		  G[y].emplace_back(x);
		};

  using P = pair<Int, Int>;
  priority_queue<P> pq;
  vector<Int> s(n,1),used(n,0);
  for(Int i=0;i<n;i++) pq.emplace(d[i],i);

  while(pq.size()>1u){
    Int x,v;
    tie(x,v)=pq.top();pq.pop();

    Int nd=x-(n-s[v])+s[v];
    if(!r.count(nd)) NO();

    Int u=r[nd];    
    if(used[u]) NO();

    add_edge(v,u);
    s[u]+=s[v];    
    used[v]=1;
  }

  vector<Int> sz(n,1);
  vector<Int> dp1(n,0),dp2(n,0);
  function<Int(Int, Int)> dfs1=
    [&](Int v,Int p){
      for(Int u:G[v]){
	if(u==p) continue;
	dp1[v]+=dfs1(u,v);
	dp1[v]+=sz[u];
	sz[v]+=sz[u];
      }
      return dp1[v];
    };

  function<void(Int, Int, Int)> dfs2=
    [&](Int v,Int p,Int d){
      Int sum=0;
      dp2[v]=dp1[v]+d;
      for(Int u:G[v])
	if(u!=p) sum+=dp1[u]+sz[u];
      for(Int u:G[v])
	if(u!=p) dfs2(u,v,d+sum-(dp1[u]+sz[u])+(n-sz[u]));
    };

