  dfs2(0,-1,0);

  //for(Int i=0;i<n;i++) cout<<d[i]<<" "<<dp2[i]<<endl;
  for(Int i=0;i<n;i++) if(d[i]!=dp2[i]) NO();

  for(Int v=0;v<n;v++)
    for(Int u:G[v]) if(v<u) cout<<v+1<<" "<<u+1<<endl;
  return 0;
}

