	}
	sort(d + 1, d + n + 1);

	for (ll i = n; i > 1; --i) {
		ll vf = d[i] - n + size[i] + size[i];
		if (vf >= d[i]) FAIL();
		fa[i] = lower_bound(d + 1, d + n + 1, vf) - d;
		if (d[fa[i]] != vf) FAIL();
		size[fa[i]] += size[i];
	}

	for (ll i = 2; i <= n; ++i)
		AddEdge(fa[i], i);
	if (!check()) FAIL();
	else for (ll i = 2; i <= n; ++i)
		printf("%lld %lld\n", id(i), id(fa[i]));
	return 0;
}

