int GET(int pos,int x){
	int l=1,r=pos,res=0;
	while(l<=r){
		int mid=(l+r)>>1;
		if(a[mid].d<=x)res=mid,l=mid+1;
		else r=mid-1;
	}
	return res;
}

void SEARCH(int x,int dep){
	sum+=dep;
	for(int r=lst[x];r;r=nxt[r]){
		SEARCH(edge[r],dep+1);
	}
}

