using namespace std;
const int N = 1e5 + 10;

int n, m;
int a[N], b[N];
LL d[N], siz[N];
map<LL, int> Map;
priority_queue<pii> Q;

void doit(int cnt) {
	pii tmp = Q.top(); Q.pop();
	int to = Map[d[tmp.se] - (n - siz[tmp.se] * 2)];
	if (!to || tmp.se == to) puts("-1"), exit(0);
	a[cnt] = to;
	b[cnt] = tmp.se;
	if (siz[tmp.se] * 2 > n) puts("-1"), exit(0);
	siz[to] += siz[tmp.se];
}

int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i) {
		scanf("%lld", &d[i]);
		Map[d[i]] = i;
		Q.push(make_pair(d[i], i));
	}
	if (n == 1 && d[1]) return 0 * puts("-1");
	for (int i = 1; i <= n; ++i)
		siz[i] = 1;
	for (int i = 1; i < n; ++i)
		doit(i);
	for (int i = 1; i < n; ++i)
		printf("%d %d\n", a[i], b[i]);
	return 0;
}

