template<typename T> inline void chkmin(T &a, T b) { if(a > b) a = b; }

int n, rt;
set<pli> D;

int f[MAXN], siz[MAXN];
LL dis[MAXN], sum[MAXN];
bool vis[MAXN];
int dex, head[MAXN], to[MAXN], nxt[MAXN];

inline void Link(int x, int y) { nxt[++dex] = head[x]; to[dex] = y; head[x] = dex; }

void dfs(int u) {
	vis[u] = 1;
	for(int i = head[u]; i; i = nxt[i]) {
		int v = to[i];
		if(vis[v]) { puts("-1"); exit(0); }
		dfs(v);
		sum[u] += sum[v] + siz[v];
	}
}

inline void init() {
	n = read<int>();
	for(int i = 1; i <= n; ++i) {
		D.insert(mp(read<LL>(), i));
	}
	rt = (*D.begin()).second;
}

inline void solve() {
	int cnt = 0;
	for(sit it = D.rbegin(); it != D.rend(); ++it) {
		int u = (*it).second;
		dis[u] = (*it).first;
		++siz[u];
		if(u == rt) break;
		LL nxt = dis[u] + (siz[u] << 1) - n;
		pli fa = (*D.lower_bound(mp(nxt, 0)));
		if(fa.first == nxt) {
//			printf("%d %d\n", fa.second, u);
//			printf("Linked\n\n");
			f[u] = fa.second;
			Link(f[u], u);
			siz[f[u]] += siz[u];
		}
//		if(siz[f[u]] == n) { rt = f[u]; break; }
	}

//	cout << rt << endl;
//	exit(0);
	dfs(rt);
	if(dis[rt] != sum[rt]) {
		puts("-1");
		exit(0);
	}
	for(int i = 1; i <= n; ++i) if(i != rt) {
		printf("%d %d\n", i, f[i]);
	}
}

int main() {

	init();
