const int MAXN = 100005;

void my_ass(bool p){
	if(!p){
		puts("-1");
		exit(0);
	}
}

int n;
pi a[MAXN];
vector<int> gph[MAXN];
set<int> s[2];
int left_size[MAXN];

lint get_dist(int x, int p, int d){
	lint ret = d;
	for(auto &i : gph[x]){
		if(i != p){
			ret += get_dist(i, x, d + 1);
		}
	}
	return ret;
}

vector<int> new_event[MAXN];

void INSERT(int val, int atleast){
	lint v = n + a[val].first - 2 * left_size[val] + 2;
	int l = lower_bound(a + 1, a + n + 1, pi(v, -1)) - a;
	new_event[max(l, atleast)].push_back(val);
}

int main(){
	scanf("%d",&n);
	for(int i=1; i<=n; i++){
		scanf("%lld",&a[i].first);
		a[i].second = i;
	}
	sort(a + 1, a + n + 1);
	left_size[1] = n;
	INSERT(1, 2);
	for(int i=2; i<=n; i++){
		for(auto &j : new_event[i]){
			s[a[j].first % 2].insert(j);
		}
		int msk = (a[i].first % 2) ^ (n % 2);
		my_ass(!s[msk].empty());
		my_ass(a[i].first <= n + a[*s[msk].begin()].first - 2);	
		int pidx = *s[msk].begin();
		gph[a[pidx].second].push_back(a[i].second);
		gph[a[i].second].push_back(a[pidx].second);
		left_size[i] = n - (a[i].first - a[pidx].first);
		left_size[i] /= 2;
		left_size[pidx] -= left_size[i];
		s[msk].erase(pidx);
		if(left_size[pidx] != 1) INSERT(pidx, i + 1);
		if(left_size[i] != 1) INSERT(i, i + 1);
	}
	for(int i=1; i<=1; i++){
		my_ass(get_dist(a[i].second, -1, 0) == a[i].first); 
	}
	for(int i=1; i<=n; i++){
		for(auto &j : gph[i]){
