inline void add(int u, int v) { e[++sum] = (Edge) { v, head[u] }, head[u] = sum; }
std::map <LL, int> mmp;

void dfs(int u, int fa) {
    for (int i = head[u]; i; i = e[i].to) {
        int v = e[i].link;
        if (v == fa) continue;
        dep[v] = dep[u] + 1, dfs(v, u);
    }
}

