        add(u, fa), add(fa, u), sz[fa] += sz[u];
        if ((++cnt) == n - 1) break;
    }
    int rt = mmp.begin()->y; dfs(rt, 0);
    LL tot = 0;
    for (int i = 1; i <= n; ++i) tot += dep[i];
    if (tot != d[rt]) dilemma;
    for (int i = 1; i <= sum; i += 2) printf("%d %d\n", e[i].link, e[i + 1].link);
    return 0;
}

