template<class T> void assign(V<T>& v, int n, const T& a = T()) { v.assign(n, a); }
template<class T, class... U> void assign(V<T>& v, int n, const U&... u) { v.resize(n); for (auto&& i : v) assign(i, u...); }

int main() {
  cin.tie(NULL); ios::sync_with_stdio(false);
  int n; cin >> n;
  if (n < 7) return cout << -1 << '\n', 0;
  V<lint> d(n); for (int i = 0; i < n; i++) cin >> d[i];
  map<lint, int> invd; for (int i = 0; i < n; i++) invd[d[i]] = i;
  set<lint> s; for (int i = 0; i < n; i++) s.insert(d[i]);
  VV<> g(n);
  while (true) {
    int v = invd[*--s.end()];
    if (!s.count(d[v] - (n - 2))) break;
    for (int i = n - 2; i > 0; i -= 2) if (s.count(d[v] - i)) {
      s.erase(d[v]);
      int w = invd[d[v] - i];
      g[v].push_back(w);
      g[w].push_back(v);
      v = w;
