int main() {
	auto no = [](){ printf("-1\n"); exit(0); };
	int n;
	scanf("%d", &n);
	vector<long long> d(n);
	map<long long, int> mp;
	rep(i, n) {
		scanf("%lld", &d[i]);
		mp[d[i]] = i;
	}
	sort(d.begin(), d.end());
	long long mi = d[0];
	reverse(d.begin(), d.end());
	d.pop_back();
	set<long long> remains;
	rep(i, d.size()) {
		remains.insert(d[i]);
	}
	vector<pair<int, int>> ans;
	rep(i, d.size()) {
		if (remains.count(d[i]) == 0) continue;
		remains.erase(d[i]);
		int s = 1;
		long long cur = d[i];
		long long prev;
		while (true) {
			long long diff = n - s * 2;
			if (diff <= 0) no();
			prev = cur;
			cur -= diff;
			if (cur == mi) {
				ans.push_back({mp[prev], mp[cur]});
				break;
			}
			if (remains.count(cur) == 0) no();
			remains.erase(cur);
			ans.push_back({mp[prev], mp[cur]});
			s ++;
		}
	}
	assert(ans.size() == n - 1);
	vector<vector<int>> g(n);
	rep(i, n - 1) {
		int a = ans[i].first;
		int b = ans[i].second;
		g[a].push_back(b);
		g[b].push_back(a);
	}
	vector<long long> s(n);
	vector<int> dis(n);
	vector<int> sz(n);
	function<void (int, int)> dfs = [&](int u, int prev) {
		sz[u] = 1;
		for (auto v : g[u]) if (v != prev) {
			dis[v] = dis[u] + 1;
			dfs(v, u);
			sz[u] += sz[v];
		}
	};
	dfs(0, -1);
	rep(i, n) s[0] += dis[i];
	function<void (int, int)> dfs2 = [&](int u, int prev) {
		for (auto v : g[u]) if (v != prev) {
			s[v] = s[u] + n - sz[v] * 2;
			dfs2(v, u);
		}
	};
	dfs2(0, -1);
	sort(s.begin(), s.end());
	d.push_back(mi);
	sort(d.begin(), d.end());
	if (s != d) {
		cout << -1 << endl;
		return 0;
	}
	for (auto it : ans) printf("%d %d\n", it.first + 1, it.second + 1);
}

