using lint = long long;

void solve() {
    int n;
    std::cin >> n;

    std::vector<lint> ds(n);
    std::map<lint, int> drev;
    for (int v = 0; v < n; ++v) {
        std::cin >> ds[v];
        drev[ds[v]] = v;
    }

    std::vector<int> vs(n);
    std::iota(vs.begin(), vs.end(), 0);
    std::sort(vs.begin(), vs.end(),
              [&](int u, int v) { return ds[u] > ds[v]; });

    std::vector<int> szs(n, 1);
    std::vector<std::vector<int>> to(n);

    for (auto v : vs) {
        if (v == vs.back()) continue;

        lint nxt = ds[v] - (n - szs[v] * 2);
        if (!drev.count(nxt)) {
            std::cout << -1 << std::endl;
            return;
        }

        int u = drev[nxt];
        to[u].push_back(v);
        szs[u] += szs[v];
    }

    int r = vs.back();

    lint dsum = 0;
    std::function<void(int, int)> dfs =
        [&](int v, int d) -> void {
        dsum += d;
        for (auto u : to[v]) dfs(u, d + 1);
    };
    dfs(r, 0);

    if (dsum != ds[r]) {
        std::cout << -1 << std::endl;
        return;
    }

    for (int v = 0; v < n; ++v) {
        for (auto u : to[v]) {
            std::cout << v + 1 << " " << u + 1 << std::endl;
        }
    }
    return;
}

int main() {
    std::cin.tie(nullptr);
    std::ios::sync_with_stdio(false);

    solve();

    return 0;
}

