ll up[100005],down[100005];
int sz[100005];

void dfs1(int x,int fa) {
  sz[x]=1;
  for(int i=head[x];i;i=e[i].next)
    if (e[i].t!=fa) {
    	int u=e[i].t;
    	dfs1(u,x);
    	down[x]+=down[u]+sz[u];
    	sz[x]+=sz[u];
	}
}

void dfs2(int x,int fa) {
  ll s=up[x];
  for(int i=head[x];i;i=e[i].next)
    if (e[i].t!=fa) s+=down[e[i].t]+sz[e[i].t];
  for(int i=head[x];i;i=e[i].next)
    if (e[i].t!=fa) {
    	int u=e[i].t;
    	up[u]=s-down[u]-sz[u]+n-sz[u];
    	dfs2(u,x);
	}
}

