	}
	p[x]=mp[t];
	size[p[x]]+=size[x];
	e[2*i-1]=Edge(x,head[p[x]]);
	head[p[x]]=2*i-1;
	e[2*i]=Edge(p[x],head[x]);
	head[x]=2*i;
  }
  dfs1(1,0);
  dfs2(1,0);
  for(int i=1;i<=n;i++)
    if (up[i]+down[i]!=num[i]) {
    	puts("-1");
    	return 0;
	}
  for(int i=1;i<=n;i++)
    if (p[i]) printf("%d %d\n",p[i],i);
  return 0;
}

