  }

  vector< int > par(N, -1), size(N, 1);
  while(que.size() >= 2) {
    auto p = *prev(que.end());
    que.erase(p);
    const int other = N - size[p.second];
    int64 latte = p.first + size[p.second] - other;

    auto it = que.lower_bound({latte, -1});
    if(it == que.end() || it->first != latte) {
      cout << -1 << endl;
      return 0;
    }
    size[it->second] += size[p.second];
    par[p.second] = it->second;
  }

  using pi = pair< int64, int >;
  auto f1 = [&](pi x, pi y) { return pi(x.first + y.first, x.second + y.second); };
  auto f2 = [](pi a, int from, int to, int d) { return pi(a.first + (a.second + 1), a.second + 1); };
  ReRooting< int, pi, decltype(f1), decltype(f2) > reroot(N, f1, f2, pi());
