using namespace std;

int main() {
	int n;
	scanf("%d", &n);
	vector<long long> d(n);
	long long miii = 1e15;
	int cent = -1;
	map<long long, int> mp;
	rep(i, n) {
		scanf("%lld", &d[i]);
		mp[d[i]] = i;
		if (miii > d[i]) {
			miii = d[i];
			cent = i;
		}
	}
	set<long long> st;
	rep(i, n) {
		if (miii != d[i]) {
			st.insert(d[i]);
		}
	}
	vector<pair<int, int>> ans;
	auto no = [](){ cout << -1 << endl; exit(0); };
	while (true) { 
		if (ans.size() >= n - 1) break;
		long long mi;
		for (auto it : st) {
			mi = it;
			break;
		}
		st.erase(mi);
		int id = mp[mi];
		ans.push_back({id, cent});
		long long dist = mi - miii;
		long long k = (n - dist) / 2;
		k --;
		dist += 2;
		long long cur = mi;
		long long prev;
		int orik = k;
		vector<int> erased;
		while (k --) {
			prev = cur;
			cur += dist;
			if (st.count(cur) == 0) {
				break;
			}
			st.erase(cur);
			erased.push_back(cur);
			int pid = mp[prev];
			int cid = mp[cur];
			ans.push_back({pid, cid});
			dist += 2;
		}
		// rollback
		bool again = false;
		if (k > -1) {
			int rb = orik - k - 1;
			while (rb --) {
				ans.pop_back();
			}
			for (auto it : erased) {
			        st.insert(it);
			}
			again = true;
		}
		if (again) {
			dist = mi - miii;
			k = (n + dist) / 2;
			k --;
			dist += 2;
			long long cur = mi;
			long long prev;
			while (k --) {
				prev = cur;
				cur += dist;
				if (st.count(cur) == 0) {
					no();
				}
				st.erase(cur);
				int pid = mp[prev];
				int cid = mp[cur];
				ans.push_back({pid, cid});
				dist += 2;
			}
		}
	}
	vector<vector<int>> g(n);
	rep(i, n - 1) {
		int a = ans[i].first;
		int b = ans[i].second;
		g[a].push_back(b);
		g[b].push_back(a);
	}
	vector<long long> s(n);
	vector<int> dis(n);
	vector<int> sz(n);
	function<void (int, int)> dfs = [&](int u, int prev) {
		sz[u] = 1;
		for (auto v : g[u]) if (v != prev) {
			dis[v] = dis[u] + 1;
			dfs(v, u);
			sz[u] += sz[v];
		}
	};
	dfs(0, -1);
	rep(i, n) s[0] += dis[i];
	function<void (int, int)> dfs2 = [&](int u, int prev) {
		for (auto v : g[u]) if (v != prev) {
			s[v] = s[u] + n - sz[v] * 2;
			dfs2(v, u);
		}
	};
	dfs2(0, -1);
	sort(s.begin(), s.end());
	sort(d.begin(), d.end());
	if (s != d) {
		cout << -1 << endl;
		return 0;
	}
	for (auto it : ans) printf("%d %d\n", it.first + 1, it.second + 1);
        return 0;
}

