void fail() {
    cout << -1 << '\n';
    exit(0);
}
int main() {
    int N; cin >> N;
    if (N == 2) fail();
    vector<Int> D(N);
    map<Int, pair<int, int>> M; // D[i] -> idx, num
    vector<vector<int>> G(N);
    for (int i = 0; i < N; i++) {
        cin >> D[i];
        M[D[i]] = make_pair(i, 1);
    }
    int root = -1;
    for (auto it = rbegin(M), ite = rend(M); it != ite; ++it) {
        int idx, num; tie(idx, num) = it->second;
        if (num == N) { root = idx; continue; }
        Int diff = num - (N - num);
        if (diff >= 0) fail();
        Int dnext = it->first + diff;
        if (!M.count(dnext)) fail();
        G[M[dnext].first].emplace_back(idx);
        M[dnext].second += num;
    }
    auto recur = [&](auto&& recur, int node) -> pair<int, int> {
        int val = 0, cnt = 1;
        for (int next : G[node]) {
            auto p = recur(recur, next);
            val += p.first + p.second;
            cnt += p.second;
        }
        return { val, cnt };
    };
    if (recur(recur, root).first != D[root]) {
        fail();
    }
    for (int i = 0; i < N; i++) {
        for (int j : G[i]) {
            cout << i + 1 << ' ' << j + 1 << '\n';
        }
    }
    return 0;
}

