void dfs(int x, int p = 0) {
	sz[x] = 1;
	ans[x] = 0;
	for (int v : g[x]) {
		if (v == p) continue;
		dfs(v, x);
		sz[x] += sz[v];
		ans[x] += sz[v] + ans[v];
	}
}

void dfs_2(int x, int p = 0) {
	if (p) {
		ans[x] += sz[p] - 2*sz[x] + ans[p] - ans[x];
		sz[x] = sz[p];
	}
	for (int v : g[x]) {
		if (v == p) continue;
		dfs_2(v, x);
	}
}


