vi children[125252];
pll po[125252];

int cnt[125252];

int sz[125252];
ll val[125252];
void dfs1(int p){
  sz[p] = 1;
  val[p] = 0;
  for(int to : children[p]){
    dfs1(to);
    sz[p] += sz[to];
    val[p] += val[to] + sz[to];
  }
}
void dfs2(int p){
  if(val[p] != d[p])fail();
  for(int to : children[p]){
    ll tmp = val[to];
    val[to] = val[p] - sz[to] + (n-sz[to]);
    dfs2(to);
    val[to] = tmp;
  }
}

